import { test, expect } from '@playwright/test';

async function getPatientData() {
    const response = await fetch(process.env.API_ENDPOINT);
    if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
    }
    return await response.json();
}

test('Dynamic patient data automation', async ({ page }) => {
    // ðŸ“¥ 1. Get dynamic data from API
    const patientData = await getPatientData();
    console.log('Patient data received:', patientData);

    // ðŸ” 2. Log in
    await page.goto('https://www.simplepractice.com');
    await page.click('text=Sign In');
    await page.fill('input[name="email"]', process.env.EMAIL);
    await page.fill('input[name="password"]', process.env.PASSWORD);
    await page.click('button[type="submit"]');

    // âœ… 3. Use dynamic patient data
    await page.goto(`https://secure.simplepractice.com/patients/${patientData.patientId}`);

    // Verify patient name
    await expect(page.locator('.patient-name')).toHaveText(patientData.name);

    // Check medications
    if (patientData.medications && patientData.medications.length > 0) {
        for (const med of patientData.medications) {
            await expect(page.locator('.medications-list')).toContainText(med);
        }
    }

    // ðŸ“¸ 4. Take evidence screenshots
    await page.screenshot({ path: 'patient-details.png' });
});
